import React from "react";

function Rules(){
    return (
        <div className="rules1">
            <h1 id="rules">Rules for the game</h1>
            <br></br>
            <h4>****Usage of hints reduces your score****</h4>
            <br></br>
            <p>1. Complete Task 1 and Task 2 to complete the game</p>
            <br></br>
            <p>2. Task1: Answer all the three questions and click the door below to get password for Task 2</p>
            <br></br>
            <p>3. After entering the password, Task 2 will be visible and complete Task 2 to win the game</p>
            <br></br>
        </div>
    );
}


export default Rules;
